public class Bus extends Vehicle {

    public Bus(int numbers, String model, int size, double speed, boolean isDriver, boolean isOpen, int seating) {
        super(numbers, model, size, speed, isDriver, isOpen, seating);
        // TODO Auto-generated constructor stub

        this.numbers = numbers;
        this.model = model;
        this.size = size;
        this.speed = speed;
        this.isDriver = isDriver;
        this.isOpen = isOpen;
        this.seating = seating;
    }

}
